mapper.select:
SELECT

FROM evidence_item
WHERE project_id = #{projectId}
AND status = #{status}
ORDER BY created_at DESC

mapper.update:
UPDATE evidence_item
title = #{title}, note = #{note}, bucket = #{bucket}, object_key = #{objectKey}, content_type = #{contentType}, size_bytes = #{sizeBytes}, etag = #{etag}, status = #{status}, invalid_reason = #{invalidReason}, invalid_by = #{invalidBy, jdbcType=OTHER, typeHandler=com.bwbcomeon.evidence.typehandler.UUIDTypeHandler}, invalid_at = #{invalidAt}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
