mapper.select:
SELECT id, project_id, stage_id, status, completed_at, created_at, updated_at
FROM project_stage
WHERE project_id = #{projectId} AND stage_id = #{stageId}

mapper.update:
UPDATE project_stage
SET status = #{status}, completed_at = #{completedAt}, updated_at = CURRENT_TIMESTAMP
WHERE project_id = #{projectId} AND stage_id = #{stageId}
